import React, { useRef, useEffect ,useContext} from 'react';
import { Container, Row, Button } from 'reactstrap';
import { NavLink, Link,useNavigate } from 'react-router-dom';
import logo from '../../assets/images/logo.png'; // Ensure path is correct
import './header.css';
import {AuthContext} from './../../context/AuthContext';


const nav_links = [
  {
    path: '/home',
    display: 'Home',
  },
  {
    path: '/about',
    display: 'About',
  },
  {
    path: '/tours',
    display: 'Tours',
  },
];

const Header = () => {
  const headerRef = useRef(null);
  const navigate=useNavigate()
  const {user,dispatch}=useContext(AuthContext)

const logout=()=>{
  dispatch({type:'LOGOUT'})
  navigate('/')
}


  const stickyHeaderFunc = () => {
    if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
      headerRef.current.classList.add('sticky_header');
    } else {
      headerRef.current.classList.remove('sticky_header');
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', stickyHeaderFunc);

    return () => {
      window.removeEventListener('scroll', stickyHeaderFunc);
    };
  }, []); // Empty dependency array to ensure this only runs once

  return (
    <header className="header" ref={headerRef}>
      <Container>
        <Row>
          <div className="nav_wrapper d-flex align-items-center justify-content-between">
            <div className="logo">
              <img src={logo} alt="Logo" /> {/* Ensure the logo is visible */}
            </div>

            {/* Menu start */}
            <div className="navigation">
              <ul className="menu d-flex align-items-center gap-5">
                {nav_links.map((item, index) => (
                  <li className="nav_item" key={index}>
                    <NavLink to={item.path} className={(navClass) => (navClass.isActive ? 'active_link' : '')}>
                      {item.display}
                    </NavLink>
                  </li>
                ))}
              </ul>
            </div>
            {/* Menu end */}

            <div className="nav_right d-flex align-items-center gap-4">
              <div className="nav_btn d-flex align-items-center gap-4">

                  



                <Button className="btn secondary_btn">
                  <Link to="/login">Login</Link>
                </Button>
                <Button className="btn primary_btn">
                  <Link to="/register">Register</Link>
                </Button>
              </div>
              <span className="mobile_menu">
                <i className="ri-menu-line"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
