import React, { useState, useEffect } from 'react';
import CommonSection from '../shared/CommonSection';
import { Container, Row, Col } from 'reactstrap';
import '../styles/tour.css';
import SearchBar from './../shared/SearchBar';
import Newsletter from './../shared/Newsletter';
import TourCard from './../shared/TourCard';
import useFetch from '../hooks/useFetch';
import { BASE_URL } from '../utils/config';

const Tours = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  // Fetch tours data and tour count
  const { data: tours, loading, error } = useFetch(`${BASE_URL}/tours?page=${page}`);
  const { data: tourCount = 0 } = useFetch(`${BASE_URL}/tours/search/getTourCount`);

  useEffect(() => {
    if (tourCount > 0) {
      const pages = Math.ceil(tourCount / 8); // Assuming 8 tours per page
      setPageCount(pages);
    }
  }, [tourCount]);

  return (
    <>
      <CommonSection title={'All Tours'} />
      
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>

      <section className="pt-0">
        <Container>
          {/* Display the total number of tours */}
          <Row>
            <Col lg="12">
              <h5 className="text-center">Total Tours: {tourCount}</h5> {/* Display tour count */}
            </Col>
          </Row>

          {loading && <h4 className="text-center pt-5">Loading.......</h4>}
          {error && <h4 className="text-center pt-5">{error}</h4>}

          {!loading && !error && (
            <Row>
              {tours?.map((tour) => (
                <Col lg="3" className="mb-4" key={tour._id}>
                  <TourCard tour={tour} />
                </Col>
              ))}

              {/* Display Pagination */}
              <Col lg="12">
                {pageCount > 0 && (
                  <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                    {[...Array(pageCount).keys()].map((number) => (
                      <span
                        key={number}
                        onClick={() => setPage(number)}
                        className={page === number ? 'active_page' : ''}
                      >
                        {number + 1}
                      </span>
                    ))}
                  </div>
                )}
              </Col>
            </Row>
          )}
        </Container>
      </section>

      <Newsletter />
    </>
  );
};

export default Tours;
