import React from 'react';
import { useParams } from 'react-router-dom';
import { Container, Row, Col } from 'reactstrap';
import tourData from '../assets/data/tours';
import '../styles/tour-details.css';

const TourDetails = () => {
  const { id } = useParams();

  // Find the specific tour from tourData
  const tour = tourData.find((tour) => tour.id === id);

  // Handle cases where the tour isn't found
  if (!tour) {
    return <h2>Tour not found!</h2>;
  }

  const { photo, title, desc, price, reviews, city, distance, maxGroupSize } = tour;

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour_content">
                <img src={photo} alt="Tour Image" />

                <div className="tour_info">
                  <h2>{title}</h2>
                  <p>{desc}</p>
                  {/* Other tour details here */}
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default TourDetails;
